mapper.select:
/* getMembershipByUserId */
SELECT A.*
FROM membership A
WHERE A.membership_id = #{membershipId}

mapper.insert:
/* insertUsage */

	SELECT NEXTVAL('membership_usage_seq')

INSERT INTO membership_usage(
	membership_usage_id, membership_id, user_id, update_date
) values(
			#{membershipUsageId}, #{membershipId}, #{userId}, NOW()
		)

mapper.update:
/* updateUsageFileSize */
UPDATE membership_usage
SET use_upload_file_size = use_upload_file_size + #{useUploadFileSize},
update_date = NOW()
WHERE user_id = #{userId}

/* updateUsageFileCount */
UPDATE membership_usage
SET use_convert_file_count = use_convert_file_count+1,
	update_date = NOW()
WHERE user_id = #{userId}
